import { Item, Nav, NavContainer, MainList } from "./Builders";
import Radio from "@material-ui/core/Radio";

const MenuSection = ({ section, children }) => (
  <SectionLink section={section}>
    {(link) => (
      <A11yItem onClick={link.onClick} selected={link.isSelected}>
        {children}
      </A11yItem>
    )}
  </SectionLink>
);

const Menu = (props) => {
  const [selectedValue, setSelectedValue] = React.useState("1");

  const handleChange = (event) => {
    setSelectedValue(event.target.value);
  };

  return (
    <div
      className='menu'
      style={{
        position: "fixed",
        top: 0,
        zIndex: 100,
      }}
    >
      <ul className='actions'>
        <div>
          <Radio
            checked={selectedValue === "1"}
            onChange={handleChange}
            value='1'
            name='radio-button'
            inputProps={{ "aria-label": "A" }}
          />
          <Radio
            checked={selectedValue === "2"}
            onChange={handleChange}
            value='2'
            name='radio-button'
            inputProps={{ "aria-label": "B" }}
          />
          <Radio
            checked={selectedValue === "3"}
            onChange={handleChange}
            value='3'
            name='radio-button'
            inputProps={{ "aria-label": "C" }}
          />
          <Radio
            checked={selectedValue === "4"}
            onChange={handleChange}
            value='4'
            name='radio-button'
            inputProps={{ "aria-label": "D" }}
          />
        </div>
        <li>
          <button onClick={() => props.moveTo(1)}>Section 1</button>
        </li>
        <li>
          <button onClick={() => props.moveTo(2)}>Section 2</button>
          <button onClick={() => props.moveTo(3)}>Section 3</button>
          <button onClick={() => props.moveTo(4)}>Section 4</button>
        </li>
      </ul>
    </div>
  );
};

export default Menu;
