import styled, { keyframes } from 'styled-components';
import { Container, Grid, Paper } from '@material-ui/core';
import { Title, UnderTitle } from '../Builders';

const Home = () => {
    return (
        <Container fixed>
            <Grid container>
                <Grid item xs={12} fullWidth>
                    <BirdContainer class='bird-container bird-container--one'>
                        <Bird class='bird bird--one'></Bird>
                    </BirdContainer>
                </Grid>
                <Grid item xs={12} fullWidth>
                    <Title>Alexander Arvebratt</Title>
                    <UnderTitle>Personal portfolio</UnderTitle>
                </Grid>

                <Grid item xs={12} fullWidth>
                    <a>
                        <StyledSpan></StyledSpan>
                        <StyledSpan></StyledSpan>
                        <StyledSpan></StyledSpan>
                    </a>
                </Grid>
            </Grid>
        </Container>
    );
};

export default Home;

const animation = keyframes`
  0% {
    opacity: 0;
  }
  50% {
    opacity: 1;
  }
  100% {
    opacity: 0;
  }

`;

export const StyledSpan = styled.span`
    position: absolute;
    width: 24px;
    height: 24px;
    border-left: 1px solid black;
    border-bottom: 1px solid black;
    -webkit-transform: rotate(-45deg);
    transform: rotate(-45deg);
    animation: ${animation} 2s infinite;
    opacity: 0;
    box-sizing: border-box;

    &:nth-of-type(1) {
        -webkit-animation-delay: 0s;
        animation-delay: 0s;
    }

    &:nth-of-type(2) {
        margin-top: 1rem;
        -webkit-animation-delay: 0.15s;
        animation-delay: 0.15s;
    }

    &:nth-of-type(3) {
        margin-top: 2rem;
        -webkit-animation-delay: 0.3s;
        animation-delay: 0.3s;
    }
`;

const BirdContainer = styled.div`
    z-index: 1;
    position: relative;
    overflow: hidden;
    display: flex;
    align-items: center;
    justify-content: center;
    min-height: 100vh;
    min-height: 35rem;
    background-image: linear-gradient(
            to bottom,
            rgba(255, 168, 76, 0.6) 0%,
            rgba(255, 123, 13, 0.6) 100%
        ),
        url('https://images.unsplash.com/photo-1446824505046-e43605ffb17f');
    background-blend-mode: soft-light;
    background-size: cover;
    background-position: center center;
    padding: 2rem;
`;

const Bird = styled.div`
    position: absolute;
    top: 20%;
    left: -10%;
    transform: scale(0) translateX(-10vw);
    will-change: transform;

    animation-name: fly-right-one;
    animation-timing-function: linear;
    animation-iteration-count: infinite;

    &--one {
        animation-duration: 15s;
        animation-delay: 0;
    }

    @keyframes fly-cycle {
        100% {
            background-position: -900px 0;
        }
    }

    @keyframes fly-right-one {
        0% {
            transform: scale(0.3) translateX(-10vw);
        }

        10% {
            transform: translateY(2vh) translateX(10vw) scale(0.4);
        }

        20% {
            transform: translateY(0vh) translateX(30vw) scale(0.5);
        }

        30% {
            transform: translateY(4vh) translateX(50vw) scale(0.6);
        }

        40% {
            transform: translateY(2vh) translateX(70vw) scale(0.6);
        }

        50% {
            transform: translateY(0vh) translateX(90vw) scale(0.6);
        }

        60% {
            transform: translateY(0vh) translateX(110vw) scale(0.6);
        }

        100% {
            transform: translateY(0vh) translateX(110vw) scale(0.6);
        }
    }
`;
